import { useEffect, useState } from "react";
import { Paper, Typography } from "@mui/material";

function HomeComponent() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    let loaded = false;
    fetch("https://back-end.egeboraerguney.workers.dev/posts/")
      .then((resp) => resp.json())
      .then((jsonResp) => {
        if (!loaded) {
          setPosts(jsonResp);
        }
      })
      .catch((err) => console.log(err));
    return () => (loaded = true);
  }, []);

  return (
    <>
      <Typography
        sx={{
          mt: "32px !important",
        }}
        variant="h4"
      >
        Share Quick Social Media App
      </Typography>
      <Button
        sx={{
          mt: "32px !important",
        }}
        variant="contained"
      >
        Create a Post
      </Button>
      {posts.map((post) => (
        <Paper
          sx={{
            mt: "32px !important",
            margin: "auto",
            width: "40%",
            padding: "16px",
          }}
          elevation={3}
        >
          <Typography variant="h5">{post.title}</Typography>
        </Paper>
      ))}
    </>
  );
}

export default HomeComponent;
