import { useEffect, useState, useRef } from "react";
import {
  Paper,
  Typography,
  Button,
  IconButton,
  Modal,
  Box,
} from "@mui/material";
import logo from "../logo.svg";
import PostComponent from "./PostComponent";
import { ThumbUp, ThumbDown } from "@mui/icons-material";

function HomeComponent() {
  const [posts, setPosts] = useState([]);
  const [postModalOpen, setPostModalOpen] = useState(false);
  const [modalPost, setModalPost] = useState({});
  const [skipFirstRender, setSkipFirstRender] = useState(true);

  function handlePostModalOpen(post) {
    setModalPost(()=>{post});
  }
  useEffect(() => {
    console.log("CALLED");
 }, [modalPost]);
  function handlePostModalClose() {
    setPostModalOpen(false);
  }
  useEffect(() => {
    /*
    let loaded = false;
    fetch("https://back-end.egeboraerguney.workers.dev/posts/")
      .then((resp) => resp.json())
      .then((jsonResp) => {
        if (!loaded) {
          setPosts(jsonResp);
        }
      })
      .catch((err) => console.log(err));
    return () => (loaded = true);
    */
    setPosts([
      {
        id: "19cd186a4d9030ad18b97c2e6d1ebbeb",
        title: "My first Post",
        username: "user1",
        content: "Hey all!",
        upvotes: 6,
        downvotes: 4,
        comments: [
          { username: "user2", content: "hey!" },
          { username: "user3", content: "Hi !" },
        ],
      },
      {
        id: "49bcee16617fde19f8e0ee5acaa0d63d",
        title: "My second Post",
        username: "user3",
        content: "Heyyo all!",
        upvotes: 10,
        downvotes: 2,
        comments: [
          { username: "user4", content: "heyyo!" },
          { username: "user53", content: "Helloo !" },
        ],
      },
      {
        id: "asdfadfadfakfamrvmaerb",
        title: "My third Post",
        username: "user345",
        content: "WoooW!",
        upvotes: 4,
        downvotes: 8,
        comments: [
          { username: "user53", content: "heyaaa!" },
          { username: "user32", content: "Hi aasdasd!" },
        ],
      },
    ]);
  }, []);

  function HandleClickVotes(postId, voteType) {
    var postToChange = posts.find((post) => post.id === postId);
    if (voteType === "up") postToChange.upvotes += 1;
    else if (voteType === "down") postToChange.downvotes += 1;
    setPosts([...posts]);
  }
  return (
    <>
      <img style={{ marginTop: "16px", width: "400px" }} src={logo} />
      <Typography
        sx={{
          mt: "0px",
        }}
        variant="h6"
      >
        Social Media Application
      </Typography>
      <Button
        sx={{
          mt: "32px !important",
        }}
        variant="contained"
      >
        Create new Post
      </Button>
      {posts.map((post) => (
        <Paper
          key={post.id}
          sx={{
            mt: "32px !important",
            margin: "auto",
            width: "40%",
            padding: "16px",
          }}
          elevation={3}
        >
          <Typography variant="h5">{post.title}</Typography>
          <Typography
            sx={{
              mt: "8px",
              mb: "8px",
            }}
            paragraph={true}
          >
            {post.content}
          </Typography>
          <Typography
            sx={{
              mt: "8px",
              mb: "16px",
            }}
            variant="h6"
          >
            <IconButton onClick={() => HandleClickVotes(post.id, "up")}>
              <ThumbUp color="primary" />
              <span style={{ marginLeft: "4px" }}>{post.upvotes}</span>
            </IconButton>
            <IconButton onClick={() => HandleClickVotes(post.id, "down")}>
              <ThumbDown sx={{ color: "red" }} />
              <span style={{ marginLeft: "4px" }}>{post.downvotes}</span>
            </IconButton>
          </Typography>
          <Typography
            variant="h6"
            sx={{
              mt: "-16px",
              float: "right",
            }}
          >
            {post.username}
          </Typography>
          <Typography
            variant="h6"
            sx={{
              mt: "-16px",
              float: "left",
            }}
            onClick={() => handlePostModalOpen(post)}
          >
            {post.comments.length} comments
          </Typography>
        </Paper>
      ))}
      <Modal open={postModalOpen} onClose={handlePostModalClose}>
        <Box sx={style}>
          <Typography variant="h6" component="h2">
            {modalPost.title}
          </Typography>
          <Typography sx={{ mt: 2 }}>{modalPost.content}</Typography>
        </Box>
      </Modal>
    </>
  );
}
const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

export default HomeComponent;
