import { useEffect, useState } from "react";
import { Paper, Typography, Button, IconButton } from "@mui/material";
import logo from "../logo.svg";
import { ThumbUp, ThumbDown } from "@mui/icons-material";

function HomeComponent() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    let loaded = false;
    fetch("https://back-end.egeboraerguney.workers.dev/posts/")
      .then((resp) => resp.json())
      .then((jsonResp) => {
        if (!loaded) {
          setPosts(jsonResp);
        }
      })
      .catch((err) => console.log(err));
    return () => (loaded = true);
  }, []);

  useEffect(() => {
    setPosts(posts);
  }, [posts]);

  function HandleClickUpvote(postId) {
    var postToChange = posts.find((post)=>post.id === postId);
    postToChange.upvotes += 1;
    console.log(posts);
    setPosts(...posts);
  }

  return (
    <>
      <img style={{ marginTop: "16px", width: "400px" }} src={logo} />
      <Typography
        sx={{
          mt: "0px",
        }}
        variant="h6"
      >
        Social Media Application
      </Typography>
      <Button
        sx={{
          mt: "32px !important",
        }}
        variant="contained"
      >
        Create new Post
      </Button>
      {posts.map((post) => (
        <Paper
          sx={{
            mt: "32px !important",
            margin: "auto",
            width: "40%",
            padding: "16px",
          }}
          elevation={3}
        >
          <Typography variant="h5">{post.title}</Typography>
          <Typography
            sx={{
              mt: "8px",
              mb: "8px",
            }}
            paragraph="true"
          >
            {post.content}
          </Typography>
          <Typography
            sx={{
              mt: "8px",
              mb: "16px",
            }}
            variant="h6"
          >
            <IconButton onClick={()=> HandleClickUpvote(post.id)}>
              <ThumbUp color="primary" />
              <span style={{marginLeft:"4px"}}>{post.upvotes}</span>
            </IconButton>
            <IconButton>
              <ThumbDown sx={{ color: "red" }} />
              <span style={{marginLeft:"4px"}}>{post.downvotes}</span>
            </IconButton>
          </Typography>
          <Typography
            variant="h6"
            sx={{
              mt: "-16px",
              float: "right",
            }}
          >
            {post.username}
          </Typography>
        </Paper>
      ))}
    </>
  );
}

export default HomeComponent;
